import React from "react";

const NewsItem=(props)=> {
    let { title, description, imgUrl, newsUrl, author, date } = props;
    return (
        <div className="my-3">
            <div className="card"> <img src={!imgUrl ? "https://images.hindustantimes.com/img/2022/06/15/1600x900/AFP_9BX2L6_1623489835537_1655275114928.jpg" : imgUrl} className="card-img-top" alt="..."/>
                <div className="card-body">
                    <h5 className="card-title">{title}</h5>
                    <span className="badge rounded-pill text-bg-secondary">
                        By {!author ? "Unknown" : author}
                    </span>
                    <p className="card-text">
                        <small className="text-muted">
                            {new Date(date).toGMTString()}
                        </small>
                    </p>
                    <p className="card-text">{description}</p>

                    <a href={newsUrl} target="_blank" className="btn">
                        Read more
                    </a>
                </div>
            </div>
        </div>
    );
  
}

export default NewsItem;
